	@Override
	public Seller findById(Integer id) {
		PreparedStatement st = null;
		ResultSet rs = null;
		try {
			st = conn.prepareStatement(
					"SELECT seller.*,department.Name as DepName "
					+ "FROM seller INNER JOIN department "
					+ "ON seller.DepartmentId = department.Id "
					+ "WHERE seller.Id = ?");
			
			st.setInt(1, id);
			rs = st.executeQuery();
			
			/*
			O resultSet retorna uma tabela (linhas coluna), e em POO precisamos tratar como objeto,
			então precisamos instanciar os objetos (department/seller) utilizando os dados que retornaram no resultSet
			*/
			if (rs.next()) {
				Department dep = new Department();
				dep.setId(rs.getInt("DepartmentId"));
				dep.setName(rs.getString("DepName"));
				
				Seller seller = new Seller();
				seller.setId(rs.getInt("Id"));
				seller.setName(rs.getString("Name"));
				seller.setEmail(rs.getString("Email"));
				seller.setBaseSalary(rs.getDouble("BaseSalary"));
				seller.setBirthDate(rs.getDate("BirthDate"));
				seller.setDepartment(dep);
				
				return seller;
			}
			
			return null;
			
		} catch (SQLException e) {
			throw new DbException(e.getMessage());
		}finally {
			/*
			 Não encerra a conexão (conn) aqui, pois pode ser que faça outros métodos ainda desse Dao, 
			 então por padrão a conexão só é fechada na aplicação depois.
			*/
			DB.closeStatement(st);
			DB.closeResultSet(rs);
		}
	}

